\n	padding-left: 5px;\n	padding-right: 5px;\n}\n\nul.topiclist dd {\n	display: block;\n	float: left;\n	padding: 4px 0;\n	border-left: 1px solid #e8e8e8;\n    transition: border-left-color 0.25s ease;\n    -webkit-transition: border-left-color 0.25s ease;\n    -moz-transition: border-left-color 0.25s ease;\n    -o-transition: border-left-color 0.25s ease;\n    -ms-transition: border-left-color 0.25s ease;\n}\nul.topiclist li:hover dd {\n	border-left-color: #d8d8d8;\n}\n\nul.topiclist dfn {\n	/* Labels for post/view counts */\n	position: absolute;\n	left: -999px;\n	width: 990px;\n}\n\nul.topiclist li.row dt a.subforum {\n	background-position: 0 50%;\n	background-repeat: no-repeat;\n	position: relative;\n	white-space: nowrap;\n	padding: 0 0 0 12px;\n}\n\nul.topiclist li.row dt a.subforum.read {\n	background-image: url("{IMG_SUBFORUM_READ_SRC}");\n}\n\nul.topiclist li.row dt a.subforum.unread {\n	background-image: url("{IMG_SUBFORUM_UNREAD_SRC}");\n}\n\n.forum-image {\n	float: left;\n	padding-top: 5px;\n	margin-right: 5px;\n}\n\n.feed-icon-forum {\n    float: right;\n    display: block;\n    width: 18px;\n    height: 18px;\n    background: url({T_THEME_PATH}/images/rss.png) 0 0 no-repeat;\n}\n.feed-icon-forum:hover { background-position: 0 -20px; }\n.feed-icon-forum span { display: none; }\n\nli.row {\n}\n\nli.row strong {\n	font-weight: normal;\n}\n\nli.header dt, li.header dd {\n    color: rgba(255, 255, 255, 0.8) !important;\n	line-height: 1em;\n	border-left-width: 0;\n	margin: 0;\n	padding: 4px 0;\n}\n.old-ie li.header dt, .old-ie li.header dd {\n    color: #fff;\n}\n\nli.header dd {\n	margin-left: 1px;\n}\n\nli.header dl {\n	min-height: 0;\n	padding-left: 6px;\n}\n\nli.header dl.icon dt {\n	/* Tweak for headers alignment when folder icon used */\n	padding-left: 0;\n	padding-right: 50px;\n}\n\n/* Forum list column styles */\ndl.icon {\n	min-height: 35px;\n	background-position: 9px 50%;		/* Position of folder icon */\n	background-repeat: no-repeat;\n}\n\ndl.icon dt {\n	padding-left: 45px;					/* Space for folder icon */\n	background-repeat: no-repeat;\n	background-position: 5px 95%;		/* Position of topic icon */\n	font-size: 11px;\n	line-height: 1.3em;\n}\ndl.icon dt div.extra {\n    float: right;\n    padding-top: 4px;\n}\n\ndd.posts, dd.topics, dd.views {\n	width: 8%;\n	text-align: center;\n	line-height: 2.2em;\n	font-size: 12px;\n}\n\na.forumtitle, a.topictitle {\n    font-size: 14px;\n    font-weight: 500;\n    line-height: 1.3em;\n    font-family: Arial, \'DejaVu Sans\', \'Liberation Sans\', Freesans, sans-serif;\n}\n.unread a.forumtitle, .unread a.forumtitle:visited, \n.unread a.topictitle, .unread a.topictitle:visited, \n.reported a.topictitle, .reported a.topictitle:visited,\n.unapproved a.topictitle, .unapproved a.topictitle:visited { \n    color: #cb296a; \n}\n.unread a.forumtitle:hover, .unread a.topictitle:hover, \n.reported a.topictitle:hover, .unapproved a.topictitle:hover  { \n    color: #cc8b29; \n}\n\n\n/* List in forum description */\ndl.icon dt ol,\ndl.icon dt ul {\n	list-style-position: inside;\n	margin-left: 1em;\n}\n\ndl.icon dt li {\n	display: list-item;\n	list-style-type: inherit;\n}\n\ndd.lastpost {\n	width: 25%;\n	font-size: 11px;\n}\n\ndd.redirect {\n	font-size: 12px;\n	line-height: 2.5em;\n}\n\ndd.redirect.empty {\n    display: none;\n}\n\ndd.moderation {\n	font-size: 11px;\n}\n\ndd.lastpost span, ul.topiclist dd.searchby span, ul.topiclist dd.info span, ul.topiclist dd.time span, dd.redirect span, dd.moderation span {\n	display: block;\n	padding-left: 5px;\n}\n\ndd.time {\n	width: auto;\n	line-height: 200%;\n	font-size: 11px;\n}\n\ndd.extra {\n	width: 12%;\n	line-height: 200%;\n	text-align: center;\n	font-size: 11px;\n}\n\ndd.mark {\n	float: right !important;\n	width: 9%;\n	text-align: center;\n	line-height: 200%;\n	font-size: 11px;\n}\n\ndd.info {\n	width: 30%;\n}\n\ndd.option {\n	width: 15%;\n	line-height: 200%;\n	text-align: center;\n	font-size: 11px;\n}\n\ndd.searchby {\n	width: 47%;\n	font-size: 11px;\n	line-height: 1em;\n}\n\nul.topiclist dd.searchextra {\n	margin-left: 5px;\n	padding: 0.2em 0;\n	font-size: 11px;\n	color: #000;\n	border-left: none;\n	clear: both;\n	width: 98%;\n	overflow: hidden;\n}\n\n/* Container for post/reply buttons */\n.topic-actions {\n	margin-bottom: 3px;\n	font-size: 11px;\n	height: 28px;\n	min-height: 28px;\n}\ndiv[class].topic-actions {\n	height: auto;\n}\n\n/* Buttons */\n.buttons {\n    float: left;\n}\n.buttons div {\n	float: left;\n	margin: 0 5px 0 0;\n	background-position: 0 100%;\n}\n.old-ie .buttons div {\n    margin-left: 10px;\n}\n.buttons div a {\n    font-size: 13px;\n    line-height: 1em;\n    white-space: nowrap;\n    display: inline-block;\n    position: relative;\n    margin: 0 0 0 10px;\n    padding: 6px 12px 0 2px;\n    height: 20px;\n    text-decoration: none;\n    text-align: center;\n    background: url({T_THEME_PATH}/images/buttons.png) top right no-repeat;\n    text-shadow: rgba(0,0,0,0.1) 1px 1px 1px;\n}\n.browser-ie .buttons div a {\n    line-height: 21.49px;\n    padding-top: 2px;\n    height: 24px;\n}\n.buttons div.locked-icon a, .buttons div.locked-icon a:visited { color: #888; }\n.buttons div.locked-icon a:hover { color: #cb296a; }\n.buttons div.locked-icon a:active { color: #cc8b29; }\n\n.buttons div a, .buttons div a:hover, .buttons div a:active {\n    text-decoration: none;\n    border-width: 0;\n    outline-style: none;\n}\n.buttons div a span {\n    display: block;\n    position: absolute;\n    top: 0;\n    bottom: 0;\n    left: -10px;\n    width: 10px;\n    background: url({T_THEME_PATH}/images/buttons.png) top left no-repeat;\n}\n\n.buttons div a:hover {\n    background-position: right -30px;\n}\n.buttons div a:active {\n    background-position: right bottom;\n}\n.buttons div a:hover span {\n    background-position: left -30px;\n}\n.buttons div a:active span {\n    background-position: left bottom;\n}\n\n\n/* Pagination\n---------------------------------------- */\n.pagination {\n	width: auto;\n	text-align: right;\n	margin-top: 5px;\n	margin-bottom: 3px;\n	float: right;\n}\n\n.pagination span.page-sep {\n	display: none;\n}\n\nli.pagination {\n	margin-top: 0;\n}\n\n.pagination strong, .pagination b {\n	font-weight: normal;\n}\n\n.pagination span strong {\n	padding: 0 2px;\n	margin: 0 2px;\n	font-weight: normal;\n    border: 1px solid #a2aec7;\n    background-color: #3083aa;\n    color: #fff;\n}\n\n.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {\n	font-weight: normal;\n	text-decoration: none;\n	margin: 0 2px;\n	padding: 0 2px;\n    border: solid 1px #ccc;\n    background-color: #f8f8f8;\n	line-height: 1.5em;\n    transition: none;\n    -webkit-transition: none;\n    -moz-transition: none;\n    -o-transition: none;\n    -ms-transition: none;\n}\n\n.pagination span a:hover {\n    border-color: #d1799c;\n    background-color: #cb296a;\n    color: #fff;\n}\n.pagination span a:active {\n    border-color: #cfaf80;\n    background-color: #cc8b29;\n    color: #fff;\n}\n\n.pagination img {\n	vertical-align: middle;\n}\n\n/* Pagination in viewforum for multipage topics */\n.row .pagination {\n	display: block;\n	float: right;\n	width: auto;\n	margin-top: 0;\n	padding: 1px 0 1px 15px;\n	font-size: 0.9em;\n	background: none 0 50% no-repeat;\n}\n\n/* Miscellaneous styles\n---------------------------------------- */\n#forum-permissions {\n	float: right;\n	width: auto;\n	padding-left: 5px;\n	margin-left: 5px;\n	margin-top: 10px;\n	text-align: right;\n}\n\n/* Login on index */\nbody.section-index #page-body > form > h3 { display: none; }\n/*\n    Layout for posts\n*/\n\n.post, .panel {\n    margin-bottom: 4px;\n    padding: 4px;\n}\n#preview {\n    margin-top: 4px;\n}\n.post > div.inner, .panel > div.inner, .old-ie .post div.inner, .old-ie .panel div.inner-panel {\n    padding: 5px 10px;\n}\n\n.online .inner { background: url("{IMG_ICON_USER_ONLINE_SRC}") 100% 0 no-repeat; }\n\n\n.postbody {\n	padding: 0;\n	line-height: 1.48em;\n	width: 76%;\n	float: left;\n	clear: both;\n	word-wrap: break-word;\n}\n\n.postbody .ignore {\n}\n\n.postbody h3.first, .postbody h3.first a {\n	/* The first post on the page uses this */\n    color: #cb296a !important;\n}\n\n.postbody h3 {\n	/* Postbody requires a different h3 format - so change it here */\n	padding: 2px 0 0 0;\n	margin: 0 0 2px 0 !important;\n	text-transform: none;\n	border: none;\n	line-height: 125%;\n	font-weight: normal;\n	font-size: 16px;\n    font-family: Arial, \'DejaVu Sans\', \'Liberation Sans\', Freesans, sans-serif;\n}\n.postbody h3, .postbody h3 a, .postbody h3 a:visited { color: #3083aa; }\n.postbody h3 a:hover { color: #cb296a; }\n\n.postbody h3 img {\n	/* Also see tweaks.css */\n	vertical-align: bottom;\n}\n\n.postbody .post-header {\n	overflow: hidden;\n}\n\n.postbody .post-header h3 {\n	float: left;\n}\n\n.postbody .content {\n	font-size: 13px;\n}\n\n.search .postbody {\n	width: 68%\n}\n\n.content {\n	min-height: 3em;\n	overflow: hidden;\n	line-height: 1.4em;\n	padding-bottom: 1px;\n}\n\n.content h2, .panel h2, .panel > div > h3, .panel .content > h3 {\n    font-family: Arial, \'DejaVu Sans\', \'Liberation Sans\', Freesans, sans-serif;\n	font-weight: normal;\n	margin: 4px 0;\n	padding: 0;\n	font-size: 16px;\n	color: #3083aa;\n}\n\n.postbody > .notice {\n    clear: left;\n    margin-top: 10px;\n    font-size: 11px;\n    font-style: italic;\n}\n\n/* Poster profile icons\n----------------------------------------*/\n.post ul.profile-icons li {\n    opacity: 0;\n    transition: opacity 0.25s ease;\n    -webkit-transition: opacity 0.25s ease;\n    -moz-transition: opacity 0.25s ease;\n    -o-transition: opacity 0.25s ease;\n    -ms-transition: opacity 0.25s ease;\n}\n.post:hover ul.profile-icons li {\n    opacity: 1;\n}\n\nul.profile-icons {\n	padding-top: 10px;\n	list-style: none;\n}\n\n/* Rollover state */\nul.profile-icons li {\n	float: left;\n	margin: 0 2px 3px 0;\n	background-position: 0 100%;\n}\n\n/* Rolloff state */\nul.profile-icons li a {\n	display: block;\n	width: 100%;\n	height: 100%;\n	background-color: #f4f4f4;\n	background-position: 0 0;\n	opacity: 1;\n}\n\n/* Hide <a> text and hide off-state image when rolling over (prevents flicker in IE) */\nul.profile-icons li span { \n    display: none; \n}\nul.profile-icons li a:hover { \n    opacity: 0;\n}\n\n/* Positioning of moderator icons */\n.postbody ul.profile-icons {\n	float: right;\n	width: auto;\n	padding: 0;\n	padding-bottom: 5px;\n}\n\n.postbody ul.profile-icons li {\n	margin: 0 3px;\n}\n\n\n/* Profile & navigation icons */\n.email-icon, .email-icon a		{ background: url("{IMG_ICON_CONTACT_EMAIL_SRC}") top left no-repeat; }\n.aim-icon, .aim-icon a			{ background: url("{IMG_ICON_CONTACT_AIM_SRC}") top left no-repeat; }\n.yahoo-icon, .yahoo-icon a		{ background: url("{IMG_ICON_CONTACT_YAHOO_SRC}") top left no-repeat; }\n.web-icon, .web-icon a			{ background: url("{IMG_ICON_CONTACT_WWW_SRC}") top left no-repeat; }\n.msnm-icon, .msnm-icon a			{ background: url("{IMG_ICON_CONTACT_MSNM_SRC}") top left no-repeat; }\n.icq-icon, .icq-icon a			{ background: url("{IMG_ICON_CONTACT_ICQ_SRC}") top left no-repeat; }\n.jabber-icon, .jabber-icon a		{ background: url("{IMG_ICON_CONTACT_JABBER_SRC}") top left no-repeat; }\n.pm-icon, .pm-icon a				{ background: url("{IMG_ICON_CONTACT_PM_SRC}") top left no-repeat; }\n.quote-icon, .quote-icon a		{ background: url("{IMG_ICON_POST_QUOTE_SRC}") top left no-repeat; }\n\n/* Moderator icons */\n.report-icon, .report-icon a		{ background: url("{IMG_ICON_POST_REPORT_SRC}") top left no-repeat; }\n.warn-icon, .warn-icon a			{ background: url("{IMG_ICON_USER_WARN_SRC}") top left no-repeat; }\n.edit-icon, .edit-icon a			{ background: url("{IMG_ICON_POST_EDIT_SRC}") top left no-repeat; }\n.delete-icon, .delete-icon a		{ background: url("{IMG_ICON_POST_DELETE_SRC}") top left no-repeat; }\n.info-icon, .info-icon a			{ background: url("{IMG_ICON_POST_INFO_SRC}") top left no-repeat; }\n\n/* Set profile icon dimensions */\nul.profile-icons li.email-icon		{ width: {IMG_ICON_CONTACT_EMAIL_WIDTH}px; height: {IMG_ICON_CONTACT_EMAIL_HEIGHT}px; }\nul.profile-icons li.aim-icon	{ width: {IMG_ICON_CONTACT_AIM_WIDTH}px; height: {IMG_ICON_CONTACT_AIM_HEIGHT}px; }\nul.profile-icons li.yahoo-icon	{ width: {IMG_ICON_CONTACT_YAHOO_WIDTH}px; height: {IMG_ICON_CONTACT_YAHOO_HEIGHT}px; }\nul.profile-icons li.web-icon	{ width: {IMG_ICON_CONTACT_WWW_WIDTH}px; height: {IMG_ICON_CONTACT_WWW_HEIGHT}px; }\nul.profile-icons li.msnm-icon	{ width: {IMG_ICON_CONTACT_MSNM_WIDTH}px; height: {IMG_ICON_CONTACT_MSNM_HEIGHT}px; }\nul.profile-icons li.icq-icon	{ width: {IMG_ICON_CONTACT_ICQ_WIDTH}px; height: {IMG_ICON_CONTACT_ICQ_HEIGHT}px; }\nul.profile-icons li.jabber-icon	{ width: {IMG_ICON_CONTACT_JABBER_WIDTH}px; height: {IMG_ICON_CONTACT_JABBER_HEIGHT}px; }\nul.profile-icons li.pm-icon		{ width: {IMG_ICON_CONTACT_PM_WIDTH}px; height: {IMG_ICON_CONTACT_PM_HEIGHT}px; }\nul.profile-icons li.quote-icon	{ width: {IMG_ICON_POST_QUOTE_WIDTH}px; height: {IMG_ICON_POST_QUOTE_HEIGHT}px; }\nul.profile-icons li.report-icon	{ width: {IMG_ICON_POST_REPORT_WIDTH}px; height: {IMG_ICON_POST_REPORT_HEIGHT}px; }\nul.profile-icons li.edit-icon	{ width: {IMG_ICON_POST_EDIT_WIDTH}px; height: {IMG_ICON_POST_EDIT_HEIGHT}px; }\nul.profile-icons li.delete-icon	{ width: {IMG_ICON_POST_DELETE_WIDTH}px; height: {IMG_ICON_POST_DELETE_HEIGHT}px; }\nul.profile-icons li.info-icon	{ width: {IMG_ICON_POST_INFO_WIDTH}px; height: {IMG_ICON_POST_INFO_HEIGHT}px; }\nul.profile-icons li.warn-icon	{ width: {IMG_ICON_USER_WARN_WIDTH}px; height: {IMG_ICON_USER_WARN_HEIGHT}px; }\n\n/* Fix profile icon default margins */\n/* ul.profile-icons li.edit-icon	{ margin: 0 0 0 3px; }\nul.profile-icons li.quote-icon	{ margin: 0 0 0 10px; }\nul.profile-icons li.info-icon, ul.profile-icons li.report-icon	{ margin: 0 3px 0 0; } */\n\n/* Post author */\np.author {\n	margin: 0;\n	padding: 0 0 5px;\n	font-size: 11px;\n	line-height: 1.2em;\n	color: #999;\n}\np.author img { margin-right: 4px; }\np.author strong { font-weight: normal; }\n\n/* Post signature */\n.signature {\n	margin-top: 1.5em;\n	padding-top: 0.2em;\n	font-size: 11px;\n	color: #888;\n	border-top: 1px solid #ddd;\n	clear: left;\n	line-height: 140%;\n	overflow: hidden;\n	width: 100%;\n}\n\ndd .signature {\n	margin: 0;\n	padding: 0;\n	clear: none;\n	border: none;\n}\n\n.signature li {\n	list-style-type: inherit;\n}\n\n.signature ul, .signature ol {\n	margin-bottom: 1em;\n	margin-left: 3em;\n}\n\n/* Poster profile block\n----------------------------------------*/\n.postprofile {\n	margin: 2px 0 10px 0;\n	min-height: 80px;\n	color: #999;\n	border-left: 1px solid #ddd;\n	width: 22%;\n	float: right;\n	word-wrap: break-word;\n}\n.pm .postprofile {\n	border-left: 1px solid #ddd;\n}\n\n.postprofile dd, .postprofile dt {\n	line-height: 1.2em;\n	margin-left: 8px;\n	font-size: 11px;\n}\n.postprofile dt {\n    font-size: 14px;\n    line-height: 1.4em;\n    font-family: Arial, \'DejaVu Sans\', \'Liberation Sans\', Freesans, sans-serif;\n}\n\n.postprofile strong {\n	font-weight: normal;\n	color: #3083aa;\n}\n\n.avatar {\n	border: none;\n	margin-bottom: 3px;\n}\n\n/* Poster profile used by search*/\n.search .postprofile {\n	width: 30%;\n}\n\n/* pm list in compose message if mass pm is enabled */\ndl.pmlist dt {\n	width: 60% !important;\n}\n\ndl.pmlist dt textarea {\n	width: 95%;\n}\n\ndl.pmlist dd {\n	margin-left: 61% !important;\n	margin-bottom: 2px;\n}\n\n/* Post poll styles\n----------------------------------------*/\nfieldset.polls dl {\n	margin-top: 5px;\n	border-top: 1px solid #ddd;\n	padding: 5px 0 0 0;\n	line-height: 120%;\n}\n\nfieldset.polls dl.voted {\n	color: #cb296a;\n}\n\nfieldset.polls dt {\n	text-align: left;\n	float: left;\n	display: block;\n	width: 30%;\n	border-right: none;\n	padding: 0;\n	margin: 0;\n	font-size: 1.1em;\n}\n\nfieldset.polls dd {\n	float: left;\n	width: 10%;\n	border-left: none;\n	padding: 0 5px;\n	margin-left: 0;\n	font-size: 1.1em;\n}\n\nfieldset.polls dd.resultbar {\n	width: 50%;\n}\n\nfieldset.polls dd input {\n	margin: 2px 0;\n}\n\nfieldset.polls dd div {\n	text-align: right;\n	font-family: Arial, Helvetica, sans-serif;\n	color: rgba(255, 255, 255, 0.8);\n	font-weight: normal;\n	padding: 0 3px;\n	overflow: visible;\n	min-width: 12px;\n	background: #3083aa url({T_THEME_PATH}/images/poll.png) 0 0 repeat-x;\n	border: solid 0px transparent;\n	border-radius: 6px;\n	-moz-border-radius: 6px;\n	-webkit-border-radius: 6px;\n	border-top-color: #eee;\n}\n.old-ie fieldset.polls dd div {\n	color: #fff;\n}\n\nfieldset.polls dd div.pollbar2 { background-position: 0 -25px; }\nfieldset.polls dd div.pollbar3 { background-position: 0 -50px; }\nfieldset.polls dd div.pollbar4 { background-position: 0 -75px; }\nfieldset.polls dd div.pollbar5 { background-position: 0 -100px; }\nfieldset.polls dl.voted dd div { background-position: 0 -125px; }\n\n\n/* BB Code styles\n----------------------------------------*/\n/* Quote block */\nblockquote {\n	background: rgba(255, 255, 255, 0.3) url({T_THEME_PATH}/images/bb_quote.gif) 5px 8px no-repeat;\n	border: 1px solid #ddd;\n	border-radius: 8px;\n	-moz-border-radius: 8px;\n	-webkit-border-radius: 8px;\n	font-size: 13px;\n	margin: 0.5em 1px 0 25px;\n	overflow: hidden;\n	padding: 5px;\n}\n\nblockquote blockquote {\n	/* Nested quotes */\n	background-color: #f7f7f7;\n	font-size: 1em;\n	margin: 0.5em 1px 0 15px;	\n}\n\nblockquote blockquote blockquote {\n	/* Nested quotes */\n	background-color: #e4e4e4;\n}\n\nblockquote cite {\n	/* Username/source of quoter */\n	font-style: normal;\n	font-weight: normal;\n	margin-left: 20px;\n	display: block;\n	font-size: 12px;\n	color: #999;\n}\n\nblockquote.uncited {\n	padding-top: 25px;\n}\n\n/* Code block */\ndl.codebox {\n	padding: 3px;\n	background-color: rgba(255, 255, 255, 0.5);\n	border: 1px solid #ddd;\n	border-radius: 8px;\n	-moz-border-radius: 8px;\n	-webkit-border-radius: 8px;\n	margin: 0.5em 1px 0 25px;\n	font-size: 1em;\n}\n\ndl.codebox dt {\n	margin-bottom: 3px;\n	padding-left: 2px;\n	font-size: 12px;\n	font-weight: normal;\n	display: block;\n	color: #999;\n}\n\nblockquote dl.codebox {\n	margin-left: 0;\n}\n\ndl.codebox code {\n	/* Also see tweaks.css */\n	overflow: auto;\n	display: block;\n	height: auto;\n	max-height: 200px;\n	white-space: normal;\n	padding: 5px 2px 2px;\n	font-family: Monaco, "Andale Mono","Courier New", Courier, mono;\n	font-size: 13px;\n	line-height: 1.3em;\n	color: #666;\n	margin: 2px 0;\n}\n\n.syntaxbg		{ color: #f7f7f7; }\n.syntaxcomment	{ color: #888; }\n.syntaxdefault	{ color: #444; }\n.syntaxhtml		{ color: #000000; }\n.syntaxkeyword	{ color: #585858; }\n.syntaxstring	{ color: #a7a7a7; }\n\n/* Attachments\n----------------------------------------*/\n.attachbox {\n	float: left;\n	width: auto; \n	margin: 5px 5px 5px 0;\n	padding: 6px;\n	background-color: rgba(255, 255, 255, 0.5);\n	border: 1px dashed #ddd;\n	border-radius: 8px;\n	-moz-border-radius: 8px;\n	-webkit-border-radius: 8px;\n	clear: left;\n	max-width: 98%;\n	max-width: calc(100% - 14px);\n	max-width: -moz-calc(100% - 14px);\n}\n\n.attachbox dt {\n	color: #999;\n	font-size: 11px;\n}\n\n.attachbox dd {\n	margin-top: 4px;\n	padding-top: 4px;\n	clear: left;\n	border-top: 1px solid #ddd;\n}\n\n.attachbox dd dd {\n	border: none;\n}\n\n.attachbox p {\n	line-height: 110%;\n	font-weight: normal;\n	clear: left;\n}\n\n.attachbox p.stats\n{\n	line-height: 110%;\n	font-weight: normal;\n	clear: left;\n}\n\n.attach-image {\n	margin: 3px 0;\n	width: 100%;\n	max-height: 350px;\n	overflow: auto;\n}\n\n.attach-image img {\n}\n\n/* Inline image thumbnails */\ndiv.inline-attachment dl.thumbnail, div.inline-attachment dl.file {\n	display: block;\n	margin-bottom: 4px;\n}\n\ndiv.inline-attachment p {\n	font-size: 100%;\n}\n\ndl.file {\n	display: block;\n}\n\ndl.file dt {\n	text-transform: none;\n	margin: 0;\n	padding: 0;\n	font-size: 12px;\n	font-weight: normal;\n}\n\ndl.file dd {\n	margin: 0;\n	padding: 0;	\n}\n\ndl.thumbnail img {\n	padding: 3px;\n	border: 1px solid #ccc;\n}\n\ndl.thumbnail dd {\n	font-style: italic;\n}\n\n.attachbox dl.thumbnail dd {\n	font-size: 100%;\n}\n\ndl.thumbnail dt a:hover {\n}\n\ndl.thumbnail dt a:hover img {\n	border: 1px solid #aaa;\n}\n\n/*\n    Reported / unapproved\n*/\np.rules {\n    background: rgba(255, 255, 255, 0.3);\n    border: solid 2px #efd7e0;\n    border-color: rgba(203, 41, 106, 0.2);\n    border-radius: 6px;\n    -moz-border-radius: 6px;\n    -webkit-border-radius: 6px;\n	padding: 6px;\n	margin: 5px 10px;\n	font-size: 12px;\n	line-height: 1.4em;\n}\n.old-ie p.rules {\n    background: #f4e9ee;\n}\np.rules:hover {\n    border-color: rgba(203, 41, 106, 0.5);\n}\n\np.rules img {\n	vertical-align: middle;\n	padding-top: 2px;\n}\np.rules strong { font-weight: normal; }\n\np.rules a {\n	vertical-align: middle;\n	clear: both;\n}\n\n/*\n    Lists bbcode\n*/\n.content ul, .content ol {\n    margin-bottom: 1em;\n    margin-left: 3em;\n}\n.content li {\n    list-style-type: inherit;\n}\n\n/*\n    Post highlight\n*/\n.posthilit { color: #cb296a; }\n\n\n/*\n	Zoom in image\n*/\nspan.zoom-container {\n	position: relative;\n	display: inline-block;\n	min-height: 34px;\n}\nspan.zoom-image { \n	display: none; \n    transition: opacity 0.25s ease;\n    -webkit-transition: opacity 0.25s ease;\n    -moz-transition: opacity 0.25s ease;\n    -o-transition: opacity 0.25s ease;\n    -ms-transition: opacity 0.25s ease;\n	opacity: 0.7;\n}\nimg.zoom + span.zoom-image {\n	display: block;\n	position: absolute;\n	top: 2px;\n	left: 2px;\n	width: 30px;\n	height: 30px;\n	background: url("{T_THEME_PATH}/images/zoom.png") 0 0 no-repeat;\n	cursor: pointer;\n}\n.zoom-container:hover span.zoom-image { opacity: 1; }\nimg.zoom.zoomed-in + span.zoom-image { \n	background-position: 0 -30px;\n	opacity: 0;\n}\n.zoom-container:hover img.zoom.zoomed-in + span.zoom-image { opacity: 0.7; }\n/*\n    Layouts for pages not covered in other css files\n*/\n\n\n/* notification */\ndiv#message, #confirm .panel, #agreement .panel {\n    margin: 40px 0;\n    font-size: 12px;\n}\ndiv#message .inner, #confirm .panel .inner, #agreement .panel .inner { \n    padding-bottom: 20px; \n}\ndiv#message h2, #confirm h2, #agreement h2 { \n    margin-left: 0; \n    margin-bottom: 20px; \n}\n\n/*\n    UCP tabs\n*/\n#tabs {\n    margin: 10px 0 6px;\n}\n#navigation, #minitabs {\n    margin: 4px 0;\n    min-height: 1em;\n}\n#tabs ul, #navigation ul, #minitabs ul {\n    list-style-type: none;\n}\n#tabs li, #navigation li, #minitabs li {\n    display: block;\n    float: left;\n}\n#tabs li a, #navigation li a, #minitabs li a {\n    display: block;\n    margin: 0;\n    padding: 0;\n    height: 24px;\n    padding-left: 10px;\n    background: url({T_THEME_PATH}/images/tabs_right.png) top left no-repeat;\n    border-width: 0;\n    outline-style: none;\n    text-decoration: none;\n}\n#tabs li a span, #navigation li a span, #minitabs li a span {\n    display: inline-block;\n    font-size: 12px;\n    line-height: 1em;\n    white-space: nowrap;\n    padding-top: 6px;\n    padding-right: 10px;\n    height: 18px;\n    background: url({T_THEME_PATH}/images/tabs_left.png) top right no-repeat;\n    color: #3083aa;\n    text-shadow: rgba(0,0,0,0.1) 1px 1px 1px;\n}\n#tabs li a:hover, #tabs li.activetab a, #navigation li a:hover, #navigation li#active-subsection a, #minitabs li a:hover, #minitabs li.activetab a {\n    background-position: bottom left;\n}\n#tabs li a:hover span, #tabs li.activetab a span, #navigation li a:hover span, #navigation li#active-subsection a span, #minitabs li a:hover span, #minitabs li.activetab a span {\n    background-position: bottom right;\n    color: #cb296a;\n}\n#tabs li.first a, #navigation li.first a, #minitabs li.first a {\n    background-image: url({T_THEME_PATH}/images/tabs_left.png);\n    padding-left: 14px;\n}\n\n#tabs li.last a, #navigation li.last a, #minitabs li.last a {\n    background-position: top right;\n    padding-right: 14px;\n    padding-left: 0;\n}\n#ta